const Dashboard = ({ currentPage, setCurrentPage, user, handleLogout, dashboardStats, sidebarOpen, setSidebarOpen }) => {
  return (
    <div className="min-h-screen flex">
      
      {/* SIDEBAR */}
      <div className={`fixed lg:static inset-y-0 left-0 z-50 w-64 bg-slate-900/60 backdrop-blur-xl border-r border-white/10 transform transition-transform duration-300 ${
        sidebarOpen ? 'translate-x-0' : '-translate-x-full lg:translate-x-0'
      }`}>

        <div className="p-6 border-b border-white/10 flex justify-between items-center">
          <div className="flex items-center space-x-2">
            <div className="w-10 h-10 bg-gradient-to-br from-purple-500 to-pink-500 rounded-lg flex items-center justify-center">
              <Brain className="w-6 h-6 text-white" />
            </div>
            <span className="text-xl font-bold text-white">HireFlow</span>
          </div>

          <button onClick={() => setSidebarOpen(false)} className="lg:hidden text-gray-400 hover:text-white">
            <X className="w-6 h-6" />
          </button>
        </div>

        {/* Navigation */}
        <nav className="p-4 space-y-2">
          {[
            { id: 'dashboard', icon: BarChart3, label: 'Dashboard' },
            { id: 'resume', icon: Upload, label: 'Resume Analyzer' },
            { id: 'interview', icon: Mic, label: 'Mock Interview' },
            { id: 'courses', icon: BookOpen, label: 'Courses' },
            { id: 'chatbot', icon: MessageSquare, label: 'AI Assistant' }
          ].map(item => (
            <button
              key={item.id}
              onClick={() => { setCurrentPage(item.id); setSidebarOpen(false); }}
              className={`w-full flex items-center space-x-3 px-4 py-3 rounded-lg transition ${
                currentPage === item.id ? 'bg-gradient-to-r from-purple-600 to-pink-600 text-white shadow-lg'
                : 'text-gray-400 hover:bg-white/5 hover:text-white'
              }`}>
              <item.icon className="w-5 h-5" />
              <span>{item.label}</span>
            </button>
          ))}
        </nav>

        {/* USER + LOGOUT */}
        <div className="absolute bottom-0 left-0 right-0 p-4 border-t border-white/10">
          <div className="flex items-center space-x-3 mb-4 px-4">
            <div className="w-10 h-10 bg-gradient-to-br from-purple-500 to-pink-500 rounded-full flex items-center justify-center text-white font-semibold">
              {user?.name?.[0]}
            </div>
            <div className="flex-1">
              <div className="text-white font-medium truncate">{user?.name}</div>
              <div className="text-gray-400 text-sm truncate">{user?.email}</div>
            </div>
          </div>
          <button onClick={handleLogout} className="w-full flex items-center space-x-3 px-4 py-2 text-gray-400 hover:text-white hover:bg-white/5 rounded-lg">
            <LogOut className="w-5 h-5" />
            <span>Logout</span>
          </button>
        </div>
      </div>

      {/* MAIN CONTENT */}
      <div className="flex-1 overflow-auto">
        
        {/* Top Bar */}
        <div className="bg-slate-900/60 backdrop-blur-xl border-b border-white/10 px-6 py-4 flex items-center justify-between sticky top-0 z-40">
          <button onClick={() => setSidebarOpen(true)} className="lg:hidden text-white">
            <Menu className="w-6 h-6" />
          </button>
          <h1 className="text-2xl font-bold text-white capitalize">
            {currentPage}
          </h1>
        </div>

        <div className="p-6">
          {currentPage === 'dashboard' && <DashboardContent stats={dashboardStats} />}
          {/* Resume / Interview / Courses / Chatbot are in Part 3 */}
        </div>
      </div>
    </div>
  );
};

